mapper.select:
WITH ITEM_WITH_CODE_USE_CHECK AS ( SELECT I.ITEM_CODE, I.ITEM_NAME, I.ITEM_GROUP_CODE, I.ITEM_CLASSIFICATION, I.UNIT_OF_STOCK, I.LOSS_RATE, I.LEAD_TIME, I.STANDARD_UNIT_PRICE, I.DESCRIPTION, C.CODE_USE_CHECK FROM ITEM I, CODE_DETAIL C WHERE I.ITEM_CODE = C.DETAIL_CODE (+) )
SELECT * FROM ITEM_WITH_CODE_USE_CHECK where 1=1
AND ITEM_CLASSIFICATION = #{itemClassification}
AND ITEM_GROUP_CODE = #{itemGroupCode}
AND STANDARD_UNIT_PRICE BETWEEN TO_NUMBER( #{minPrice} ) AND TO_NUMBER ( #{maxPrice} )

select standard_unit_price
from item
where item_code = #{itemCode}

select standard_unit_price
from item
where item_code = #{itemCode}
