mapper.delete:
DELETE FROM CERTIFICATE 
WHERE EMP_CODE=#{empCode} 
AND REQUEST_DATE=TO_DATE(#{requestDate},'YYYY-MM-DD')

mapper.update:

UPDATE CERTIFICATE 
SET	APPROVAL_STATUS=#{approvalStatus},  REJECT_CAUSE=#{rejectCause}
WHERE EMP_CODE = #{empCode} 
AND REQUEST_DATE = TO_DATE(#{requestDate},'YYYY-MM-DD')
